document.addEventListener("DOMContentLoaded",()=>{const e=document.querySelector(".animated__scroll"),t=e.querySelector(".animated__scroll__track"),n=t.querySelectorAll(".slide"),o=Math.PI,l=document.documentElement.clientWidth;let c,d=t.getBoundingClientRect().height,i=75;const r=()=>{n.forEach(e=>{c=2*o*$(e).offset().left/l,e.style=`margin-top: ${32.5*Math.sin(c)+32.5}px`;const n=t.getBoundingClientRect().height;i+=d-n,t.style=`padding-bottom: ${i}px`})};l>1e3&&(e.addEventListener("scroll",r),r())});